// ここにあなたのAPIキーを入れてください
const API_KEY = "AIzaSyDgk6lak3XsiBE1y6zTdy5D5kJSSYNXy9o"; 

async function checkModels() {
  const url = `https://generativelanguage.googleapis.com/v1beta/models?key=${API_KEY}`;
  
  try {
    const response = await fetch(url);
    const data = await response.json();
    
    if (data.models) {
      console.log("=== あなたが現在利用可能なモデル一覧 ===");
      data.models.forEach(m => {
        // "generateContent" に対応しているモデルだけ表示
        if (m.supportedGenerationMethods.includes("generateContent")) {
            console.log(m.name); // ここに表示された名前をコピーして使ってください
        }
      });
    } else {
      console.log("モデルが見つかりませんでした。エラー内容:", data);
    }
  } catch (error) {
    console.error("通信エラー:", error);
  }
}

checkModels();